###########################################         Butterworth Filter             ###########################################import numpy as npfrom scipy.signal import butter, filtfiltdef butter_filter(data, lpass, order):    b, a = butter(N = order, Wn = lpass * 2, btype = 'low', analog=False)    y = filtfilt(b, a, data)    return(y)def get_the_butter(climate_array, lpass, order):    # Define new empty array    new_array = np.zeros(climate_array.shape)    # Sample each column     for cols in range(climate_array.shape[1]):        new_vec = butter_filter(data = climate_array[:,cols], lpass = lpass, order = order)        new_array[:,cols] = new_vec            return(new_array)
